import { stub, mock } from 'sinon';
import * as userModel from '../models/UserModel';
import { createToken, hashPassword } from '../helper';

const User = userModel.userModel;

const invalidData = {
    _id: 'id',
    email: 'email',
    password: 'pwd'
}

const validData = {
    _id: 'id',
    email: 'test@gmail.com',
    password: 'pwd'
}

const wrongPwd = {
    _id: 'id',
    email: 'test@gmail.com',
    password: 'pw'
}

describe('User Resolver Tests', () => { 
    it('getUsers', async () => {

        let mock:any = stub(User, 'find');
        mock.returns({
            ...validData
        });

        const result:any = await getUsers();

        expect(result.email).to.equal(validData.email);    
        expect(result.password).to.equal(validData.password);   

        mock.restore();

    });

    it('createUser', async () => {

        let mockFind: any = stub(User, 'findOne');
        let mockSave: any = stub(User.prototype, 'save');

        mockFind.throws();

        mockFind.returns({
            ...validData
        }); 

        mockSave.returns({
            ...validData
        });   

        let result: any = createUser({ userInput: invalidData }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('Email is invalid.');    
            });


        result = createUser({ userInput: validData }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('User already exists.');
            });        

        mockFind.returns(false);  

        result = await createUser({ userInput: validData }, 'sdf');

        expect(result.email).to.equal(validData.email);
        expect(result.password).to.equal(validData.password);

        mockFind.restore();
        mockSave.restore();

    });

    it('login', async () => {

        const hashedPwd = await hashPassword(validData.password);        

        validData.password = hashedPwd;

        let mockFind: any = stub(User, 'findOne');

        mockFind.throws();

        mockFind.returns(false); 

        login({ userInput: invalidData }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('Email is invalid.');    
            });

        login({ userInput: validData }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('User does not exist.');    
            });

        mockFind.returns({
            ...validData
        }); 

        login({ userInput: wrongPwd }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('You have entered wrong password.');
            });

        validData.password = 'pwd';

        const result = await login({ userInput: validData }, 'sdf');
        const token = createToken(validData._id, validData.email);

        expect(result.token).to.equal(token);
        expect(result.userId).to.equal(validData._id);

        mockFind.restore();

    });

    it('validateUser', async () => {
        const token = createToken(validData._id, validData.email);
        const data = {
            token,
            userId: validData._id
        }

        const invalidData = {
            token,
            userId: 'a'
        }

        const validResult = validateToken({ userInput: data }, 'sdf');
        expect(validResult.isValid).to.equal(true);

        // const invalidResult = validateToken({ userInput: invalidData }, 'sdf');
        // expect(invalidResult.isValid).to.equal(false);

    });


})

import validator from 'validator';
import * as userModel from '../models/UserModel';
import { compareHash, compareToken, createToken, hashPassword } from '../helper';

const User = userModel.userModel;

interface Token {     
    userId: string,
    email: string,
    iat: string,
    exp: string

}
export const getUsers = async () => {
    return User.find({});    
}

export const createUser = async ({ userInput }, req) => {

    const { email, password } = userInput;

    if (!validator.isEmail(email)) {
        throw new Error("Email is invalid.");
    }

    const result = await User.findOne({ email });

    if (result) {
        throw new Error("User already exists.");
    }
    const hashedValue = await hashPassword(password);
    const user = new User({ email, password: hashedValue });

    return user.save();

}

export const login = async ({ userInput }, req) => {

    const { email, password } = userInput;

    if (!validator.isEmail(email)) {
        throw new Error("Email is invalid.");
    }

    const result = await User.findOne({ email });

    if (!result) {
        throw new Error("User does not exist.");            
    }
    const valid = await compareHash(password, result.password);

    if (!valid) { 
        throw new Error("You have entered wrong password.");
    }
    return {
        userId: result._id.toString(),
        token: createToken(result._id.toString(), result.email)
    }
}

export const validateToken = ({ userInput }, req) => {
    const { token, userId } = userInput;
    return {
        isValid: compareToken(userId, token)
    } 

}

import { buildSchema } from 'graphql';

export const userSchema =  buildSchema(`

    input UserInput {
        email: String!
        password: String!
    }

    input ValidateInputType {
        token: String! 
        userId: String!
    }

    type User {
        _id: ID!
        email: String!
        password: String!
        lastLogin: String
    }

    type ValidateType {
        isValid: Boolean!        
    }

    type AuthData {
        userId: ID!
        token: String!
    }

    type UserMethods {
        getUsers: [User!]
    }

    type UserMutation {
        createUser(userInput: UserInput) : User!
        login(userInput: UserInput): AuthData!
        validateToken(userInput: ValidateInputType): ValidateType!
    }

    schema {
        query: UserMethods
        mutation: UserMutation
    }

`);

import bcrypt from 'bcryptjs';
import  * as jwt from 'jsonwebtoken';
import config from './config';

export const createToken = (userId: string, email: string) => { 
    return jwt.sign({
        userId: userId,
        email: email
    }, config.jwt,
        {
            expiresIn: '1h'
        }
    )
}   

export const compareToken = (userId:string, token: string) => { 
    const decoded: any = jwt.verify(token, config.jwt);
    if (decoded.userId === userId.toString()) { 
        return true;
    }
    return false;
}   

export const hashPassword = (password:string) => { 
    return bcrypt.hash(password, 12);
}

export const compareHash = (inputPassword:string, realPassword:string) => { 
    return bcrypt.compare(inputPassword, realPassword)
}

