import { expect } from 'chai';
import { compareHash, createToken, createUser, getUsers, hashPassword, login, validateToken } from './userResolver';
import { stub, mock } from 'sinon';
import * as userModel from '../models/UserModel';
import  * as jwt from 'jsonwebtoken';

const User = userModel.userModel;


describe('User Tests', () => { 
    it('getUsers', async () => {

        let mock:any = stub(User, 'find');
        mock.returns({
            email: 'email',
            password: 'pwd'
        });

        const result:any = await getUsers();

        expect(result.email).to.equal('email');    
        expect(result.password).to.equal('pwd');    
    });

    it('createUser', async () => {

        let mockFind: any = stub(User, 'findOne');
        let mockSave: any = stub(User.prototype, 'save');

        mockFind.throws();

        mockFind.returns({
            email: 'test@gmail.com',
            password: 'pwd'
        }); 

        mockSave.returns({
            email: 'test@gmail.com',
            password: 'pwd'
        });   

        const invalidInput = {
            email: 'test',
            password: 'pwd'
        };

        const validInput = {
            email: 'test@gmail.com',
            password: 'pwd'
        };

        let result: any = createUser({ userInput: invalidInput }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('Email is invalid.');    
            });


        result = createUser({ userInput: validInput }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('User already exists.');
            });        

        mockFind.returns(false);  

        result = await createUser({ userInput: validInput }, 'sdf');

        expect(result.email).to.equal('test@gmail.com');
        expect(result.password).to.equal('pwd');

        mockFind.restore();

    });

    it('login', async () => {

        let mockFind: any = stub(User, 'findOne');

        mockFind.throws();

        mockFind.returns(false); 
        const hashedPwd = await hashPassword('pwd');

        const invalidInput = {
            email: 'test',
            password: 'pwd'
        };


        const validInput = {
            email: 'test@gmail.com',
            password: 'pwd'
        };

        login({ userInput: invalidInput }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('Email is invalid.');    
            });

        login({ userInput: validInput }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('User does not exist.');    
            });

        const wrongPwd = {
            email: 'test@gmail.com',
            password: 'pw'
        };

        mockFind.returns({
            _id: 'id',
            email: 'test@gmail.com',
            password: hashedPwd
        }); 

        login({ userInput: wrongPwd }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('You have entered wrong password.');
            });

        const result = await login({ userInput: validInput }, 'sdf');
        const token = createToken('id', 'test@gmail.com');

        expect(result.token).to.equal(token);
        expect(result.userId).to.equal('id');

        mockFind.restore();

    });

    it('validateUser', async () => {
        const token = createToken('id', 'test@gmail.com');

        const validInput = {
            token: token,
            userId: 'id'
        };

        const invalidInpput = {
            token: token,
            userId: 'i'
        };

        const result = validateToken({ userInput: validInput }, 'sdf');
        expect(result.isValid).to.equal(true);

        const result2 = validateToken({ userInput: invalidInpput }, 'sdf');
        expect(result2.isValid).to.equal(false);


    });


})

import validator from 'validator';
import * as userModel from '../models/UserModel';
import bcrypt from 'bcryptjs';
import  * as jwt from 'jsonwebtoken';

const User = userModel.userModel;

interface Token {     
    userId: string,
    email: string,
    iat: string,
    exp: string

}
export const getUsers = async () => {
    return User.find({});    
}

export const createUser = async ({ userInput }, req) => {

    const { email, password } = userInput;

    if (!validator.isEmail(email)) {
        throw new Error("Email is invalid.");
    }

    const result = await User.findOne({ email });

    if (result) {
        throw new Error("User already exists.");
    }
    const hashedValue = await hashPassword(password);
    const user = new User({ email, password: hashedValue });

    return user.save();

}

export const login = async ({ userInput }, req) => {

    const { email, password } = userInput;

    if (!validator.isEmail(email)) {
        throw new Error("Email is invalid.");
    }

    const result = await User.findOne({ email });

    if (!result) {
        throw new Error("User does not exist.");            
    }
    const valid = await compareHash(password, result.password);

    if (!valid) { 
        throw new Error("You have entered wrong password.");
    }
    return {
        userId: result._id.toString(),
        token: createToken(result._id.toString(), result.email)

    }

}

export const validateToken = ({ userInput }, req) => { 
    const { token, userId } = userInput;
    return {
        isValid: compareToken(userId, token)
    } 

} 

export const createToken = (userId:string, email: string) => { 
    return jwt.sign({
        userId: userId,
        email: email
    }, 'veenayakAggarwal1998',
        {
            expiresIn: '1h'
        }
    )
}   

export const compareToken = (userId:string, token: string) => { 
    const decoded: any = jwt.verify(token, 'veenayakAggarwal1998');
    if (decoded.userId === userId.toString()) { 
        return true;
    }
    return false;
}   

export const hashPassword = (password:string) => { 
    return bcrypt.hash(password, 12);
}

export const compareHash = (inputPassword:string, realPassword:string) => { 
    return bcrypt.compare(inputPassword, realPassword)
}

import { NextFunction, Request, Response } from 'express';
import { Schema, model, Date } from 'mongoose';

interface User {
    email: string;
    password?: string;
    lastLogin: Date;
    token: string
}

const userSchema = new Schema<User>({

    email:  { 
        type: String, 
        index: true, 
        unique: true,
        required: true
    },
    password: {
        type: String, 
        required: true
    },
    lastLogin : {
        type : Date,
        default: Date.now()
    },
    token: {
        type : String
    }

});

export const userModel = model<User>('users', userSchema);

export const insertUser =  (req:any, res:Response, next:NextFunction) => {
    const data = { email: req.body.email, password: req.body.password };
    const userData = new userModel(data);

    return userData.save()
        .then(result => result)
        .catch(err => {
            const error = new Error(err);
            error.message = "There is some issue in creating this account";

            if (err.index === 0) { 
                error.message = "User already exists";
            }                        
            return next(error);
        });

}

export const fetchUser = () => {
    return userModel.find({})
        .then(data => data)       
        .catch(err => err)
}

export const fetchUserByEmail = (req:Request ,res:Response, next:NextFunction) => {
    return userModel.find({ email: req.params.key })
        .then(result => result)       
        .catch(err => {
            return next(Error(err));
        });
}

export const updateUser = (req:Request, res:Response, next:NextFunction) => {
    const data = { email: req.body.email, password: req.body.password };                
    const filter = { email: req.params.key };

    return userModel.updateOne(filter, data)
        .then(result => result)       
        .catch(err => {
            return next(Error(err));
        });
}

export const deleteUser = (req:Request, res:Response, next:NextFunction) => {
    return userModel.deleteOne({ email: req.params.key })
        .then(data => data)       
        .catch(err => {
            return next(Error(err));
        });
}
