import { expect } from 'chai';
import { createUser, getUsers } from './userResolver';
import { stub } from 'sinon';
import * as userModel from '../models/UserModel';
const User = userModel.userModel;


describe('User Tests', () => { 
    it('getUsers', async () => {

        let mock:any = stub(User, 'find');
        mock.returns({
            email: 'email',
            password: 'pwd'
        });

        const result:any = await getUsers();

        expect(result.email).to.equal('email');    
        expect(result.password).to.equal('pwd');    
    });

    it('createUser', async () => {

        let mockFind: any = stub(User, 'findOne');

        mockFind.throws();

        mockFind.returns({
            email: 'test@gmail.com',
            password: 'pwd'
        });       

        const invalidInput = {
            email: 'test',
            password: 'pwd'
        };

        const validInput = {
            email: 'test@gmail.com',
            password: 'pwd'
        };

        let result: any = await createUser({ userInput: invalidInput }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('Email is invalid.');    
            });


        result = await createUser({ userInput: validInput }, 'sdf')
            .catch(err => {
                expect(err.message).to.equal('User already exists.');
            });

        mockFind.restore();
    });


})

import validator from 'validator';
import * as userModel from '../models/UserModel';
import bcrypt from 'bcryptjs';
import  * as jwt from 'jsonwebtoken';

const User = userModel.userModel;

interface Token {     
    userId: string,
    email: string,
    iat: string,
    exp: string

}
export const getUsers = async () => {
    return User.find({});    
}

export const createUser = async ({ userInput }, req) => {

    const { email, password } = userInput;

    if (!validator.isEmail(email)) {
        throw new Error("Email is invalid.");
    }

    const result = await User.findOne({ email });

    if (result) {
        throw new Error("User already exists.");
    }
    return hashPassword(password)
        .then(hashedValue => {
            const data = { email, hashedValue };
            const user = new User(data);
            return user.save();
        });

}

export const login = async ({ userInput }, req) => {

    const { email, password } = userInput;

    if (!validator.isEmail(email)) {
        throw new Error("Email is invalid.");
    }

    return User.findOne({ email })
        .then(data => {
            if (!data) {
                throw new Error("User does not exist.");            
            }
            return compareHash(password, data.password)
                .then(valid => {
                    if (!valid) { 
                        throw new Error("You have entered wrong password.");
                    }
                    return {
                        userId: data._id.toString(),
                        token: createToken(data._id, data.email)
                    }
                });
        });        
}

export const validateToken = ({ userInput }, req) => { 
    const { token, userId } = userInput;
    return {
        isValid: compareToken(userId, token)
    } 

} 

const createToken = (id:Object, email: string) => { 
    return jwt.sign({
        userId: id.toString(),
        email: email
    }, 'veenayakAggarwal1998',
        {
            expiresIn: '1h'
        }
    )
}   

const compareToken = (userId:Object, token: string) => { 
    const decoded: any = jwt.verify(token, 'veenayakAggarwal1998');
    if (decoded.userId === userId.toString()) { 
        return true;
    }
    return false;
}   

const hashPassword = (password:string) => { 
    return bcrypt.hash(password, 12);
}

const compareHash = (inputPassword:string, realPassword:string) => { 
    return bcrypt.compare(inputPassword, realPassword)
}

import { buildSchema } from 'graphql';

export const userSchema =  buildSchema(`

    input UserInput {
        email: String!
        password: String!
    }

    input ValidateInputType {
        token: String! 
        userId: String!
    }

    type User {
        _id: ID!
        email: String!
        password: String!
        lastLogin: String
    }

    type ValidateType {
        isValid: Boolean!        
    }

    type AuthData {
        userId: ID!
        token: String!
    }

    type UserMethods {
        getUsers: [User!]
    }

    type UserMutation {
        createUser(userInput: UserInput) : User!
        login(userInput: UserInput): AuthData!
        validateToken(userInput: ValidateInputType): ValidateType!
    }

    schema {
        query: UserMethods
        mutation: UserMutation
    }

`);

import { NextFunction, Request, Response } from 'express';
import { Schema, model, Date } from 'mongoose';

interface User {
    email: string;
    password?: string;
    lastLogin: Date;
    token: string
}

const userSchema = new Schema<User>({

    email:  { 
        type: String, 
        index: true, 
        unique: true,
        required: true
    },
    password: {
        type: String, 
        required: true
    },
    lastLogin : {
        type : Date,
        default: Date.now()
    },
    token: {
        type : String
    }

});

export const userModel = model<User>('users', userSchema);

export const insertUser =  (req:any, res:Response, next:NextFunction) => {
    const data = { email: req.body.email, password: req.body.password };
    const userData = new userModel(data);

    return userData.save()
        .then(result => result)
        .catch(err => {
            const error = new Error(err);
            error.message = "There is some issue in creating this account";

            if (err.index === 0) { 
                error.message = "User already exists";
            }                        
            return next(error);
        });

}

export const fetchUser = () => {
    return userModel.find({})
        .then(data => data)       
        .catch(err => err)
}

export const fetchUserByEmail = (req:Request ,res:Response, next:NextFunction) => {
    return userModel.find({ email: req.params.key })
        .then(result => result)       
        .catch(err => {
            return next(Error(err));
        });
}

export const updateUser = (req:Request, res:Response, next:NextFunction) => {
    const data = { email: req.body.email, password: req.body.password };                
    const filter = { email: req.params.key };

    return userModel.updateOne(filter, data)
        .then(result => result)       
        .catch(err => {
            return next(Error(err));
        });
}

export const deleteUser = (req:Request, res:Response, next:NextFunction) => {
    return userModel.deleteOne({ email: req.params.key })
        .then(data => data)       
        .catch(err => {
            return next(Error(err));
        });
}

import { Router } from "express";
import * as userController from '../controllers/UserController';

const routes = Router();

routes.get('/users', userController.getUser);
routes.get('/users/:key', userController.getUserByKey);
routes.post('/users', userController.postUser);
routes.put('/users/:key', userController.putUser);
routes.delete('/users/:key', userController.deleteUser);

export default routes;
